import { Link, animateScroll } from 'react-scroll';
import { useRef, useEffect, useState } from 'react';
import styles from './styles.module.css';
import GithubIcon from '../GithubIcon';
import LinkedinIcon from '../LinkedinIcon';

const Home = () => {
  const ref = useRef(null);
  const [current, setCurrent] = useState('bottom');
  const scroll = animateScroll;

  const scrollTo = () => {
    if (top) {
      if (current === 'top') return;
      console.log('teste2');
      setCurrent('top');
      scroll.scrollTo(0);
    } else {
      if (current === 'bottom') return;
      console.log('teste');
      setCurrent('bottom');
      scroll.scrollTo(window.innerHeight);
    }
  };

  useEffect(() => {
    if (current === 'top') {
      console.log('teste2');
      setCurrent('top');
      scroll.scrollTo(0);
    } else {
      console.log('teste');
      setCurrent('bottom');
      scroll.scrollTo(window.innerHeight);
    }
  }, [current]);

  useEffect(() => {
    let lastScrollTop = 0;
    window.addEventListener(
      'scroll',
      function (e) {
        if (e.scrollY === lastScrollTop) return;

        if (this.scrollY < lastScrollTop) {
          console.log('Cima');
          setCurrent('top');
        } else {
          console.log('Baixo');
          setCurrent('bottom');
        }
        lastScrollTop = this.scrollY;
      },
      false,
    );
  }, [scroll]);

  return (
    <div ref={ref} className={` el ${styles.container}`}>
      <div className={styles.avatar}>
        <img
          className={styles.img}
          src="https://rsdias-storage.s3.amazonaws.com/rafael.jpg"
          alt="Rafael Dias"
        />
      </div>

      <div className={styles.content}>
        <div className={styles.about}>
          <div className={styles.me}>
            <span>Dias</span>
            <h1>Rafael</h1>
            <div className={styles.info}>
              <div className={styles.occupation}>
                <h2>Web Developer</h2>
              </div>
            </div>
          </div>
          <div className={styles.social}>
            <LinkedinIcon width="56px" height="56px" fill="#fff" />
            <GithubIcon width="56px" height="56px" fill="#fff" />
          </div>
        </div>
        <div className={styles.contact}>
          <div className={styles.item}>
            <p>Location</p>
            <p>SÃ£o Paulo</p>
          </div>
          <div className={styles.item}>
            <p>Phone</p>
            <p>(98) 981278401</p>
          </div>
          <div className={styles.item}>
            <p>Email</p>
            <p>contato@rafaelsdias.dev</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
