import { useRef, useState } from 'react';
import Prismic from '@prismicio/client';
import { getPrismicClient } from '../../services/prismic';
import { RichText } from 'prismic-dom';
import styles from './styles.module.css';
import GithubIcon from '../GithubIcon';
import LinkedinIcon from '../LinkedinIcon';
import ExpandMore from '../../images/expand_more.svg';
import Photo from '../../images/rafael.jpg';

const Home = () => {
  const [data, setData] = useState(null);
  const ref = useRef(null);
  const prismic = getPrismicClient();

  const getUser = async () => {
    const response = await prismic.query(
      [Prismic.predicates.at('document.type', 'user-info')],
      {
        fetch: [
          'user-info.photo',
          'user-info.first-name',
          'user-info.last-name',
          'user-info.occupation',
          'user-info.photo',
          'user-info.location',
          'user-info.phone',
          'user-info.email',
          'user-info.github',
          'user-info.linkedin',
        ],
        pageSize: 100,
      },
    );

    const userData = response.results.map((user) => {
      return {
        slug: user.uid,
        firstName: RichText.asText(user.data['first-name']),
        lastName: RichText.asText(user.data['last-name']),
        occupation: RichText.asText(user.data.occupation),
        phone: RichText.asText(user.data.phone),
        photo: user.data.photo.url,
        location: RichText.asText(user.data.location),
        email: RichText.asText(user.data.email),
        github: RichText.asText(user.data.github),
        linkedin: RichText.asText(user.data.linkedin),
      };
    });

    setData(userData);
  };

  getUser();

  const changeSlide = () => {
    window.dispatchEvent(
      new KeyboardEvent('keydown', {
        keyCode: 40,
      }),
    );
  };

  return (
    <div ref={ref} className={styles.container}>
      {data?.map((user) => (
        <>
          <div className={styles.avatar}>
            <img
              className={styles.avatar__image}
              src={Photo}
              alt={user.firstName}
            />
          </div>

          <div className={styles.content}>
            <div className={styles.content__about}>
              <div className={styles.content__me}>
                <span className={styles['me__last-name']}>{user.lastName}</span>
                <h1 className={styles['me__first-name']}>{user.firstName}</h1>
                <div className={styles.occupation}>
                  <h2 className={styles.occupation__name}>{user.occupation}</h2>
                </div>
              </div>
              <div className={styles.social}>
                <a href={user.linkedin}>
                  <LinkedinIcon width="56px" height="56px" fill="#fff" />
                </a>
                <a href={user.github}>
                  <GithubIcon width="56px" height="56px" fill="#fff" />
                </a>
              </div>
            </div>
            <ul className={styles.list}>
              <li className={styles.list__item}>
                <p className={styles.item__paragraph}>Location</p>
                <p className={styles.item__paragraph}>{user.location}</p>
              </li>
              <li className={styles.list__item}>
                <p className={styles.item__paragraph}>Phone</p>
                <p className={styles.item__paragraph}>{user.phone}</p>
              </li>
              <li className={styles.list__item}>
                <p className={styles.item__paragraph}>Email</p>
                <p className={styles.item__paragraph}>user.email</p>
              </li>
            </ul>
          </div>
          <div onClick={changeSlide} className={styles.scroll}>
            <img src={ExpandMore} alt="expand more" />
          </div>
        </>
      ))}
    </div>
  );
};

export default Home;
